ms : [];        $group_result_items = [];        $has_group = $has_single_rule = 0;        // Сортируем условия по старшинству        $conditions = self::sort_by_precedence($conditions);        // Определяем, что перед нами: набор или нет        $is_bundle = $level === 0 && shopFlexdiscountData::isBundle() && $group_andor == 'and';        $bundle_items = null;        foreach ($conditions as $c) {            // Если перед нами группа скидок, разбираем ее            if (isset($c->group_op)) {                $has_group = 1;                // Если мы ищем доступные скидки для наборов, необходимо производить нестрогую проверку условий (ИЛИ)                if ($is_bundle && $available_discount) {                    $group_andor = 'or';                }                $result = self::filter_items($items, $c->group_op, $c->conditions, $level + 1);                if ($group_andor == 'and' && !$result) {                    $result_items = $group_result_items = [];                    // Если отбор товаров не прошел, сбрасываем набор                    if ($is_bundle) {                        self::resetBundle();                    }                    break;                }                // Добавляем товары в набор                if ($is_bundle) {                    self::addToBundle($result);                }                $group_result_items += $result;            } else {                $has_single_rule = 1;                if ($has_group) {                    $result_items = $group_result_items;                    $has_group = 0;                }                // Проверяем работоспособность оператора                if (isset($c->op) && !self::prepare_operator($c->op)) {                    continue;                }                // Фильтруем товары по типу                $function_name = 'filter_by_' . $c->type;                if (method_exists($instance, $function_name)) {                    $filtered_items = self::executeFilterFunction($function_name, $group_andor == 'and' ? $result_items : $items, $c);                    if ($group_andor == 'and') {                        $result_items = $filtered_items;                        if (!$result_items) {                            // Если отбор товаров не прошел, сбрасываем набор                            if ($is_bundle) {                                self::resetBundle();                            }                            break;                        }                    } else {                        $result_items += $filtered_items;                    }                }                if ($is_bundle) {                    $bundle_items = $result_items;                }            }        }        if ($has_group && !$has_single_rule) {            $result_items = $group_result_items;        }        // Добавляем товары в набор        if ($is_bundle && $bundle_items !== null) {            self::addToBundle($bundle_items);        }        return $result_items;    }    /**     * Use details filter     *     * @param array $details     * @param array $items     * @return array     */    protected static function detailsFilter($details, $items)    {        $product_price = ['total_quantity' => 0];        $n_filters = [            'cheapest', 'expensive', 'ncheapest', 'nexpensive',            'ncheapest2', 'nexpensive2', 'nsubsiquent'        ];        foreach ($items as $k => $item) {            //  Каждый n-одинаковый товар            if ($details->field == 'every') {                $every = floor($item['quantity'] / $details->value);                if ($every > 0) {                    $items[$k]['quantity'] = $every;                } else {                    unset($items[$k]);                }            } // Каждые n-дешевых товаров или Каждые n-дорогих товаров или Каждый n-й товар (самый дешевый) или Каждый n-й товар (самый дорогой)            elseif (in_array($details->field, $n_filters)) {                $price = !empty($details->price_type) && $details->price_type == 'compare_price' && $item['compare_price'] > 0 ? (float) $item['compare_price'] : (float) $item['price'];                if (!isset($product_price[$price])) {                    $product_price[$price] = [];                }                $product_price[$price][] = [                    'quantity' => $item['quantity'],                    'item_id' => $k                ];                $product_price['total_quantity'] += $item['quantity'];            } // Каждый одинаковый последующий за n-товаром            elseif ($details->field == 'subsiquent') {                if ($item['quantity'] > $details->value) {                    $items[$k]['quantity'] -= $details->value;                } else {                    unset($items[$k]);                }            } // Скидка на n-товаров из z-одинаковых            elseif ($details->field == 'multiple') {                $mult = floor($item['quantity'] / $details->value[1]);                if ($mult > 0) {                    $quantity = $mult * $details->value[0];                    $items[$k]['quantity'] = $quantity > $item['quantity'] ? $item['quantity'] : $quantity;                } else {                    unset($items[$k]);                }            }        }        if (in_array($details->field, $n_filters)) {            $total_quantity = $product_price['total_quantity'];            unset($product_price['total_quantity']);            if (count($product_price) > 0) {                if (in_array($details->field, ['cheapest', 'ncheapest', 'nexpensive'])) {                    ksort($product_price);                } else {                    krsort($product_price);                }                if (in_array($details->field, ['ncheapest', 'nexpensive2'])) {                    $details->value = floor($total_quantity / $details->value);                }                if (!in_array($details->field, ['ncheapest2', 'nexpensive', 'nsubsiquent'])) {                    foreach ($product_price as $prod_p) {                        foreach ($prod_p as $pp) {                            if ($details->value <= 0) {                                unset($items[$pp['item_id']]);                                continue;                            }                            if ($pp['quantity'] <= $details->value) {                                $details->value -= $pp['quantity'];                            } else if ($details->value < $pp['quantity']) {                                $items[$pp['item_id']]['quantity'] = $details->value;                                $details->value = 0;                            }                        }                    }                } elseif ($details->value > 0) {                    $item_ids_row = [];                    $details->value = (int) $details->value;                    foreach ($product_price as $prod_p) {                        foreach ($prod_p as $pp) {                            $item_ids_row = array_merge($item_ids_row, array_fill(0, $pp['quantity'], $pp['item_id']));                        }                    }                    $i = 1;                    foreach ($item_ids_row as $item_id) {                        if ($details->field !== 'nsubsiquent') {                            if ($i++ % $details->value !== 0) {                                if ($items[$item_id]['quantity'] > 1) {                                    $items[$item_id]['quantity']--;                                } else {                                    unset($items[$item_id]);                                }                            }                        } else {                            if ($details->value > 1) {                                $details->value--;                                if ($items[$item_id]['quantity'] > 1) {                                    $items[$item_id]['quantity']--;                                } else {                                    unset($items[$item_id]);                                }                            }                        }                    }                }            }        }        return $items;    }    /**     * Execute filter function. If function is not filtered, then save it results to cache.     * Cache contains bool values. If true - return all items, false - return empty array     *     * @param string $function_name     * @param array $items     * @param array $params     * @return mixed     */    private static function executeFilterFunction($function_name, $items, $params)    {        $cache_results = shopFlexdiscountApp::get('runtime.condition_results', []);        // Функции, которые не являются фильтрующими        $not_filtered_conditions = array(            'filter_by_services' => 1,            'filter_by_num_items' => 1,            'filter_by_sum' => 1,            'filter_by_total' => 1,            'filter_by_total_feat' => 1,            'filter_by_prod_each_price' => 1,            'filter_by_ucat' => 0,            'filter_by_user' => 0,            'filter_by_user_date' => 0,            'filter_by_user_birthday' => 0,            'filter_by_user_data' => 0,            'filter_by_customer_source' => 0,            'filter_by_all_orders' => 0,            'filter_by_order_int' => 0,            'filter_by_count_orders' => 0,            'filter_by_order_count_int' => 0,            'filter_by_order_prod' => 0,            'filter_by_date' => 0,            'filter_by_week' => 0,            'filter_by_time' => 0,            'filter_by_cookie' => 0,            'filter_by_session' => 0,            'filter_by_get' => 0,            'filter_by_post' => 0,            'filter_by_server' => 0,            'filter_by_storefront' => 0        );        $params = (array) $params;        $settings = shopFlexdiscountApp::get('settings');        $functions = shopFlexdiscountApp::getFunction();        if ((!empty($settings['cache_conditions']) || !isset($settings['cache_conditions'])) && isset($not_filtered_conditions[$function_name]) && !waRequest::param('flexdiscount_skip_caching', 0)) {            $hash = $not_filtered_conditions[$function_name] ? $functions->getRequestHash($function_name, self::getProductIds($items), $params) : $functions->getRequestHash($function_name, $params);            if (!isset($cache_results[$hash])) {                $result = self::$function_name($items, $params);                $cache_results[$hash] = $result ? 1 : 0;                (new shopFlexdiscountApp)->set('runtime.condition_results', $cache_results);            }            return $cache_results[$hash] ? $items : array();        }        return self::$function_name($items, $params);    }    /**     * Use bundle filter     *     * @param array $items     * @param array $target     * @return array     */    protected static function bundleFilter($items, &$target)    {        /*         * Данная переменная содержит массив комплектов. Каждый новый массив - это отобранные товары из группы условий.         * В дальнейшем, перебирая эти массивы мы найдем общее кол-во комплектов (мультипликатовр)         * */        $bundle = self::getBundle();        if ($bundle) {            $mult = 9999999999;            // Рассчитываем, сколько комплектов в корзине. На основе кол-ва комплектов вычисляем мультипликатор.            foreach ($bundle as $b) {                $quantity = 0;                foreach ($b as $b1) {                    $quantity += (float) $b1['quantity'];                }                $mult = min($mult, floor($quantity / $b1['bundle_quantity']));            }            // Если товары не группируются в комплект, прерываем обработку.            if (!$mult) {                return array();            }            if ($target->target == 'all_true') {                $bundle_items = array();                $sku_items = waUtils::getFieldValues($items, 'id', 'sku_id');                foreach ($bundle as $b) {                    $first_elem = reset($b);                    // Для каждого комплекта находим подходящее количество товаров, на которое нужно установить скидку, согласно мультипликатору.                    // Отбираем самые дешевые товары                    $bundle_items += self::detailsFilter((object) array('field' => 'cheapest', 'value' => $mult * $first_elem['bundle_quantity']), array_intersect_key($b, $sku_items));                }            }            foreach ($items as $k => $item) {                if ($target->target == 'all_true') {                    $items[$k]['quantity'] = isset($bundle_items[$item['sku_id']]) ? $bundle_items[$item['sku_id']]['quantity'] : 0;                } else {                    $items[$k]['quantity'] = $items[$k]['quantity'] > $mult ? $mult : $items[$k]['quantity'];                }                $items[$k]['bundle_mult'] = $mult;            }            // Если необходимо установить скидку на к-л категорию или товар, ограничиваем кол-во товаров мультипликатором.            if (($target->target !== 'all_true' && $target->target !== 'all_false') && (empty($target->details->field) || empty($target->details->value))) {                $target->details = (object) array('field' => 'cheapest', 'value' => $mult);            }            return $items;        }        return array();    }    /**     * Sort conditions by precedence.     * Groups (-1) always should be the first, then other rules, sorted by precedence.     *     * @param array $conditions     * @return array     */    private static function sort_by_precedence($conditions)    {        $sorted_conditions = array(-1 => array(), 0 => array(), 1 => array(), 2 => array(), 3 => array());        foreach ($conditions as $c) {            $c = (object) $c;            if (isset($c->group_op)) {                $sorted_conditions[-1][] = $c;            } else {                if (isset($c->type) && isset(self::$type_precedence[$c->type])) {                    $sorted_conditions[self::$type_precedence[$c->type]][] = $c;                }            }        }        $result = array();        foreach ($sorted_conditions as $sc) {            $result = array_merge($result, $sc);        }        return $result;    }    /*     * **     * Товар     * * */    private static function _filter_by_type($items, $params, $all = false, $type = 'cat')    {        if (!empty($params['value'])) {            $filter_by_type = $type == 'cat' ? self::getCategoryProducts($params['value'], $all, $items) : ($type == 'set' ? self::getSetProducts($params['value'], $items) : $params['value']);            foreach ($items as $k => $item) {                $id = $type == 'type' ? (isset($item['type_id']) ? (int) $item['type_id'] : (isset($item['product']['type_id']) ? (int) $item['product']['type_id'] : 0)) : shopFlexdiscountApp::getHelper()->getProductId($item);                if (!self::execute_operator($params['op'], $id, $filter_by_type)) {                    unset($items[$k]);                }            }        }        return $items;    }    protected static function filter_by_cat($items, $params)    {        return self::_filter_by_type($items, $params);    }    protected static function filter_by_cat_all($items, $params)    {        return self::_filter_by_type($items, $params, true);    }    protected static function filter_by_set($items, $params)    {        return self::_filter_by_type($items, $params, false, 'set');    }    protected static function filter_by_type($items, $params)    {        return self::_filter_by_type($items, $params, false, 'type');    }    protected static function filter_by_services($items, $params)    {        $all_services = array();        foreach ($items as $item) {            if (!empty($item['product_services'])) {                foreach ($item['product_services'] as $service_id => $serv) {                    foreach ($serv as $s) {                        $all_services[$service_id][$s['service_variant_id']] = $s['service_variant_id'];                    }                }            }        }        if (!empty($params['field']) && empty($params['value'])) {            $service = $params['field'];            $services = array_keys($all_services);        } elseif (!empty($params['field']) && !empty($params['value'])) {            $service = $params['value'];            $services = !empty($all_services[$params['field']]) ? $all_services[$params['field']] : array();        } else {            return $items;        }        if (self::execute_operator($params['op'], $service, $services)) {            return $items;        }        return array();    }    protected static function filter_by_product($items, $params)    {        if (!empty($params['value'])) {            foreach ($items as $k => $item) {                if (!self::execute_operator($params['op'], shopFlexdiscountApp::getHelper()->getProductId($item, $params['product_type']), $params['value'])) {                    unset($items[$k]);                }            }        }        return $items;    }    private static function _filter_by_feature($items, $feature_id, $operator, $value)    {        $result_items = $products = array();        // Если не было передано характеристики, прерываем обработку        if (!$feature_id) {            return $items;        }        // Выбираем товары        foreach ($items as $k => $item) {            $items[$k]['product_id'] = shopFlexdiscountApp::getHelper()->getProductId($item);        }        // Получаем все характеристики и присваиваем товарам их характеристики        $features = self::getFeatures($items);        if ($features) {            foreach ($items as $k => $item) {                $product = $item['product'];                // Если характеристика выпадающая                if (!empty($features[$feature_id]['selectable'])) {                    // Значение должно быть двойное: первая часть - ID характеристики, вторая - ID значения                    if (strpos($value, "-") !== false) {                        $parts = explode("-", $value);                        // Проверка. Если ID характеристики в значении не совпадает с переданным ID, значит целостность нарушена.                        if ($parts[0] !== $feature_id) {                            continue;                        }                        $value_id = $parts[1];                        // Значение характеристик товара                        $product_feature_value = isset($product['feature_values'][$feature_id]) ? (is_array($product['feature_values'][$feature_id]) ? array_keys($product['feature_values'][$feature_id]) : $product['feature_values'][$feature_id]) : 0;                        $item_sku_id = shopFlexdiscountApp::getHelper()->getProductId($item, 'sku');                        if ((empty($product['selectable_features'][$item_sku_id][$feature_id]) && self::execute_operator($operator, $product_feature_value, $value_id)) || (!empty($product['selectable_features'][$item_sku_id][$feature_id]) && self::execute_operator($operator, $product['selectable_features'][$item_sku_id][$feature_id], $value_id))) {                            $result_items[$k] = $items[$k];                        }                    } else {                        continue;                    }                } else {                    // Если у товара существует характеристика, и она является объектом                    if (isset($product['features'][$feature_id]) && is_object($product['features'][$feature_id])) {                        // Если это диапазон, то обработку операторов будем выполнять специально для диапазона значений                        if ($product['features'][$feature_id] instanceof shopRangeValue) {                            if (self::execute_operator($operator, array($product['features'][$feature_id]->begin_base_unit, $product['features'][$feature_id]->end_base_unit), $value, true)) {                                $result_items[$k] = $items[$k];                            }                        } else {                            $product_feature_value = '';                            if (property_exists($product['features'][$feature_id], 'value_base_unit')) {                                $product_feature_value = $product['features'][$feature_id]->value_base_unit;                            } elseif (property_exists($product['features'][$feature_id], 'value')) {                                $product_feature_value = $product['features'][$feature_id]->value;                            }                            // Обрабатываем другие объекты                            if (self::execute_operator($operator, $product_feature_value, $value)) {                                $result_items[$k] = $items[$k];                            }                        }                    } else {                        // Обработка других типов характеристик                        $product_feature_value = isset($product['features'][$feature_id]) ? $product['features'][$feature_id] : '';                        if (self::execute_operator($operator, $product_feature_value, $value)) {                            $result_items[$k] = $items[$k];                        }                    }                }            }        }        return $result_items;    }    protected static function filter_by_feature($items, $params)    {        return self::_filter_by_feature($items, $params['field'], $params['op'], $params['value']);    }    protected static function filter_by_params($items, $params)    {        if (!empty($params['field'])) {            (new shopFlexdiscountApp())->set('runtime.shop/products', shopFlexdiscountApp::getHelper()->prepareShopProducts($items));            foreach ($items as $k => $item) {                $product_id = shopFlexdiscountApp::getHelper()->getProductId($item);                $product_params = shopFlexdiscountData::getProductParams($product_id);                if (!self::execute_operator($params['op'], $params['value'], isset($product_params[$params['field']]) ? $product_params[$params['field']] : '')) {                    unset($items[$k]);                }            }            return $items;        }        return array();    }    /*     * **     * Корзина     * * */    protected static function filter_by_num($items, $params)    {        return self::_filter_by_num($items, $params['op'], $params['value']);    }    private static function _filter_by_num($items, $operator, $value)    {        $quantity = 0;        if ($items) {            foreach ($items as $item) {                $quantity += $item['quantity'];            }        }        if (!self::execute_operator($operator, $quantity, $value)) {            return array();        }        return $items ? $items : array(self::getAbstractProduct());    }    protected static function filter_by_num_total($items, $params)    {        $result_items = self::_filter_by_num(self::getAllItems(null), $params['op'], $params['value']);        return $result_items ? $items : array();    }    private static function filter_by_num_prod($items, $params)    {        $products = $product_items = array();        // Фильтруем по товару        if ($params['field']) {            $items = self::filter_by_product($items, array("op" => "eq_num", "value" => $params['field'], "product_type" => $params['product_type']));        }        $quantity = 0;        $is_bundle = shopFlexdiscountData::isBundle();        // Добавляем информацию о количестве в набор        if ($is_bundle) {            $params['op'] = 'gte';            self::addToBundle(null, $params['value']);        }        if ($items) {            foreach ($items as $k => $item) {                $id = shopFlexdiscountApp::getHelper()->getProductId($item, $params['product_type']);                // Если необходимо просуммировать кол-во артикулов                if ($params['sum_type'] == 'sum' && $params['product_type'] !== 'sku') {                    $products[$id] = !isset($products[$id]) ? (int) $item['quantity'] : $products[$id] + (int) $item['quantity'];                    $product_items[$id][] = $k;                } else {                    if (!self::execute_operator($params['op'], $item['quantity'], $params['value'])) {                        unset($items[$k]);                    }                }            }            // Для просуммированных артикулов проверяем значения            if ($products) {                foreach ($products as $product_id => $quantity) {                    if (!self::execute_operator($params['op'], $quantity, $params['value'])) {                        foreach ($product_items[$product_id] as $i) {                            unset($items[$i]);                        }                    }                }            }        } else {            // Если отфильтрованного товара не существует, проверяем, возможно пользователь установил кол-во равное нулю            if (self::execute_operator($params['op'], $quantity, $params['value'])) {                return !$is_bundle ? array(self::getAbstractProduct()) : array();            }        }        return $items;    }    private static function _filter_by_num_type($items, $params, $all = false, $type = 'cat')    {        if (!empty($params['ext'])) {            $products = $product_items = array();            // Фильтруем по товару            if ($params['field']) {                $items = self::filter_by_product($items, array("op" => "eq_num", "value" => $params['field'], "product_type" => $params['product_type']));                if (!$items) {                    return array();                }            }            $filter_by_type = $type == 'cat' ? self::getCategoryProducts($params['ext'], $all, $items) : ($type == 'set' ? self::getSetProducts($params['ext'], $items) : $params['ext']);            foreach ($items as $k => $item) {                $id = shopFlexdiscountApp::getHelper()->getProductId($item, $params['product_type']);                // Если необходимо просуммировать кол-во артикулов                if ($params['sum_type'] == 'sum' && $params['product_type'] !== 'sku') {                    $products[$id] = !isset($products[$id]) ? (int) $item['quantity'] : $products[$id] + (int) $item['quantity'];                    $product_items[$id][] = $k;                } // Если каждый артикул рассчитывается отдельно                else {                    $quantity = 0;                    if ($type == 'type') {                        $type_id = isset($item['type_id']) ? (int) $item['type_id'] : (isset($item['product']['type_id']) ? (int) $item['product']['type_id'] : 0);                    }                    if (self::execute_operator($params['op'], $type == 'type' ? $type_id : $id, $filter_by_type)) {                        $quantity += $item['quantity'];                    }                    if (!self::execute_operator($params['op2'], $quantity, $params['value'])) {                        unset($items[$k]);                    }                }            }            // Для просуммированных артикулов проверяем значения            if ($products) {                foreach ($products as $product_id => $quantity) {                    if ($type == 'type') {                        $item = $items[reset($product_items[$product_id])];                        $type_id = isset($item['type_id']) ? (int) $item['type_id'] : (isset($item['product']['type_id']) ? (int) $item['product']['type_id'] : 0);                    }                    if (!self::execute_operator($params['op'], $type == 'type' ? $type_id : $product_id, $filter_by_type)) {                        $quantity = 0;                    }                    if (!self::execute_operator($params['op2'], $quantity, $params['value'])) {                        foreach ($product_items[$product_id] as $i) {                            unset($items[$i]);                        }                    }                }            }            return $items;        }        return array();    }    private static function filter_by_num_cat($items, $params)    {        return self::_filter_by_num_type($items, $params);    }    private static function filter_by_num_cat_all($items, $params)    {        return self::_filter_by_num_type($items, $params, true);    }    private static function filter_by_num_set($items, $params)    {        return self::_filter_by_num_type($items, $params, false, 'set');    }    private static function filter_by_num_type($items, $params)    {        return self::_filter_by_num_type($items, $params, false, 'type');    }    private static function _filter_by_num_all_type($items, $params, $all = false, $type = 'cat')    {        if (!empty($params['field'])) {            $is_bundle = shopFlexdiscountData::isBundle();            // Добавляем информацию о количестве в набор            if ($is_bundle) {                self::addToBundle(null, $params['value']);            }            $result_items = self::_filter_by_type($items, array('op' => $params['op'], 'value' => $params['field']), $all, $type);            return !$is_bundle ? self::_filter_by_num($result_items, $params['op2'], $params['value']) : $result_items;        }        return array();    }    private static function filter_by_num_all_cat($items, $params)    {        return self::_filter_by_num_all_type($items, $params);    }    private static function filter_by_num_all_cat_all($items, $params)    {        return self::_filter_by_num_all_type($items, $params, true);    }    private static function filter_by_num_all_set($items, $params)    {        return self::_filter_by_num_all_type($items, $params, false, 'set');    }    private static function filter_by_num_all_type($items, $params)    {        return self::_filter_by_num_all_type($items, $params, false, 'type');    }    private static function filter_by_num_feat($items, $params)    {        if (!empty($params['field']) && !empty($params['ext'])) {            $is_bundle = shopFlexdiscountData::isBundle();            // Добавляем информацию о количестве в набор            if ($is_bundle) {                self::addToBundle(null, $params['value']);            }            $result_items = self::_filter_by_feature($items, $params['field'], $params['op'], $params['ext']);            return !$is_bundle ? self::_filter_by_num($result_items, $params['op2'], $params['value']) : $result_items;        }        return array();    }    private static function filter_by_num_items($items, $params)    {        if (!self::execute_operator($params['op'], count($items), $params['value'])) {            return array();        }        return $items;    }    private static function filter_by_sum($items, $params)    {        return self::_filter_by_sum($items, $params['op'], $params['value']);    }    private static function _filter_by_sum($items, $operator, $value)    {        $total_price = 0;        foreach ($items as $item) {            $total_price += $item['primary_price'] * $item['quantity'];        }        if (!self::execute_operator($operator, $total_price, $value)) {            return array();        }        return $items;    }    private static function _filter_by_sum_cat($items, $params, $all = false)    {        if (!empty($params['field'])) {            $total_price = 0;            $filter_by_type = self::getCategoryProducts($params['field'], $all, $items);            foreach ($items as $k => $item) {                $product_id = shopFlexdiscountApp::getHelper()->getProductId($item);                if (self::execute_operator('eq', $product_id, $filter_by_type)) {                    $total_price += $item['primary_price'] * $item['quantity'];                } else {                    unset($items[$k]);                }            }            if (self::execute_operator($params['op'], $total_price, $params['value'])) {                return $items;            }        }        return array();    }    private static function filter_by_sum_cat($items, $params)    {        return self::_filter_by_sum_cat($items, $params);    }    private static function filter_by_sum_cat_all($items, $params)    {        return self::_filter_by_sum_cat($items, $params, true);    }    private static function filter_by_sum_feat($items, $params)    {        if (!empty($params['field']) && !empty($params['ext'])) {            $result_items = self::_filter_by_feature($items, $params['field'], $params['op'], $params['ext']);            if ($result_items) {                return self::_filter_by_sum($result_items, $params['op2'], $params['value']);            }        }        return array();    }    private static function filter_by_total($items, $params)    {        $app = new shopFlexdiscountApp();        $order = $app::get('order.info');        $total = $app::getFunction()->shop_currency($order['total'], $order['currency'], $app::get('system')['primary_currency'], false);        if (!self::execute_operator($params['op'], $total, $params['value'])) {            return array();        }        return $items;    }    private static function filter_by_total_with_discount($items, $params)    {        $app = new shopFlexdiscountApp();        $functions = $app::getFunction();        $order = $app::get('order.info');        if (!empty($order['id'])) {            $total = $functions->shop_currency($order['total'] - $functions->floatval($order['discount']), $order['currency'], $app::get('system')['primary_currency'], false);        } else {            // Учитываем плагин "Купить в 1 клик" (quickorder)            // Работает не очень хорошо            if (waRequest::param('plugin', '') == 'quickorder') {                $order_params = $app::getOrder()->getCurrentOrderParams();                $total = $order_params['quickorder_cart']->getType() == 'cart' ? waRequest::param('flexdiscount_shop_cart_total') : $order_params['quickorder_cart']->getTotal(false, true);            } else {                // Игорь, если у тебя есть сомнения насчет данной конструкции, лучше ее не удалять.                $total = waRequest::param('flexdiscount_shop_cart_total');            }            if ($total === null) {                if (!waRequest::param('flexdiscount_shop_cart_total_magic')) {                    waRequest::setParam('igaponov_force_calculate', 1);                    waRequest::setParam('flexdiscount_skip_caching', 1);                    waRequest::setParam('flexdiscount_shop_cart_total_magic', 1);                }                return [];            } else {                waRequest::setParam('igaponov_force_calculate', 0);                waRequest::setParam('flexdiscount_shop_cart_total_magic', 0);            }            $total = $functions->shop_currency($total, $app::get('system')['current_currency'], $app::get('system')['primary_currency'], false);        }        if (!self::execute_operator($params['op'], $total, $params['value'])) {            return [];        }        return $items;    }    private static function filter_by_total_feat($items, $params)    {        if (!empty($params['field'])) {            $feature_id = $params['field'];            // Суммируем значение характеристики            $feature = 0;            // Получаем все характеристики и присваиваем товарам их характеристики            self::getFeatures($items);            foreach ($items as $it) {                $product = $it['product'];                $item_sku_id = shopFlexdiscountApp::getHelper()->getProductId($it, 'sku');                if (isset($product['selectable_features'][$item_sku_id])) {                    if (!isset($product['selectable_features'][$item_sku_id][$feature_id])) {                        continue;                    }                    $feature_sku_value = $product['selectable_features'][$item_sku_id][$feature_id];                    if (isset($product['features'][$feature_id][$feature_sku_value])) {                        // Если у товара существует характеристика, и она является объектом                        if (is_object($product['features'][$feature_id][$feature_sku_value]) && $product['features'][$feature_id][$feature_sku_value] instanceof shopDimensionValue) {                            $feature += $product['features'][$feature_id][$feature_sku_value]->value_base_unit * $it['quantity'];                        } // Обработка других типов характеристик                        else {                            $feature += shopFlexdiscountApp::getFunction()->floatVal($product['features'][$feature_id][$feature_sku_value]) * $it['quantity'];                        }                    }                } else {                    // Если у товара существует характеристика, и она является объектом                    if (isset($product['features'][$feature_id])) {                        // Обработка массива                        if (is_array($product['features'][$feature_id])) {                            foreach ($product['features'][$feature_id] as $pff) {                                if (is_object($pff) && $pff instanceof shopDimensionValue) {                                    $feature += $pff->value_base_unit * $it['quantity'];                                }                            }                        } // Обработка объекта shopDimensionValue                        elseif (is_object($product['features'][$feature_id]) && $product['features'][$feature_id] instanceof shopDimensionValue) {                            $feature += $product['features'][$feature_id]->value_base_unit * $it['quantity'];                        } else {                            // Обработка других типов характеристик                            $feature += shopFlexdiscountApp::getFunction()->floatVal($product['features'][$feature_id]) * $it['quantity'];                        }                    }                }            }            if (self::execute_operator($params['op'], $feature, $params['value'])) {                return $items;            }        }        return array();    }    /*     * **     * Свойства товара     * * */    private static function _filter_by_product_ternary($items, $params, $name, $strip_tags = false)    {        foreach ($items as $k => $item) {            if (!self::execute_operator($params['op'], isset($item['product'][$name]) ? ($strip_tags ? strip_tags($item['product'][$name]) : $item['product'][$name]) : '', $params['value'])) {                unset($items[$k]);            }        }        return $items;    }    private static function filter_by_product_name($items, $params)    {        return self::_filter_by_product_ternary($items, $params, 'name');    }    private static function filter_by_product_summary($items, $params)    {        return self::_filter_by_product_ternary($items, $params, 'summary', true);    }    private static function filter_by_product_mt($items, $params)    {        return self::_filter_by_product_ternary($items, $params, 'meta_title');    }    private static function filter_by_product_mk($items, $params)    {        return self::_filter_by_product_ternary($items, $params, 'meta_keywords');    }    private static function filter_by_product_md($items, $params)    {        return self::_filter_by_product_ternary($items, $params, 'meta_description');    }    private static function filter_by_product_description($items, $params)    {        return self::_filter_by_product_ternary($items, $params, 'description', true);    }    private static function filter_by_product_rating($items, $params)    {        return self::_filter_by_product_ternary($items, $params, 'rating');    }    private static function filter_by_product_rating_count($items, $params)    {        return self::_filter_by_product_ternary($items, $params, 'rating_count');    }    private static function filter_by_product_min_price($items, $params)    {        return self::_filter_by_product_ternary($items, $params, 'min_price');    }    private static function filter_by_product_max_price($items, $params)    {        return self::_filter_by_product_ternary($items, $params, 'max_price');    }    private static function filter_by_product_total_sales($items, $params)    {        $period = shopFlexdiscountHelper::getPeriod(!empty($params['period_type']) ? $params['period_type'] : 'alltime', $params);        $params['sum_type'] = isset($params['sum_type']) ? $params['sum_type'] : 'not_sum';        (new shopFlexdiscountApp())->set('runtime.shop/products', shopFlexdiscountApp::getHelper()->prepareShopProducts($items));        // Получаем продажи для товаров        $total_sales = self::getTotalSales($items, $period, $params['sum_type']);        if ($total_sales) {            foreach ($items as $k => $item) {                if (!self::execute_operator($params['op'], $total_sales[shopFlexdiscountApp::getHelper()->getProductId($item, $params['sum_type'] == 'not_sum' ? 'sku' : 'product', isset($item['id']) ? $item['id'] : 0)], $params['value'])) {                    unset($items[$k]);                }            }        } else {            return array();        }        return $items;    }    private static function filter_by_product_number_sales($items, $params)    {        $period = shopFlexdiscountHelper::getPeriod(!empty($params['period_type']) ? $params['period_type'] : 'alltime', $params);        $params['sum_type'] = isset($params['sum_type']) ? $params['sum_type'] : 'not_sum';        (new shopFlexdiscountApp())->set('runtime.shop/products', shopFlexdiscountApp::getHelper()->prepareShopProducts($items));        // Получаем продажи для товаров        $total_number_sales = self::getTotalNumberSales($items, $period, $params['sum_type']);        if ($total_number_sales) {            foreach ($items as $k => $item) {                if (!self::execute_operator($params['op'], $total_number_sales[shopFlexdiscountApp::getHelper()->getProductId($item, $params['sum_type'] == 'not_sum' ? 'sku' : 'product', isset($item['id']) ? $item['id'] : 0)], $params['value'])) {                    unset($items[$k]);                }            }        } else {            return array();        }        return $items;    }    private static function filter_by_product_stock_change($items, $params)    {        $action = ifempty($params, 'field', 'increase');        $stock_logs = self::getProductStockChange($items, $params['sum_type'], $action);        $params['value'] = ifempty($params['value'], 0);        $period = shopFlexdiscountHelper::getPeriod('ndays', array('field1' => $params['value']));        $start = strtotime($period['start']);        foreach ($items as $k => $item) {            $key = $params['sum_type'] . '-' . $action . '-d--' . ($params['sum_type'] == 'not_sum' ? '-s' . shopFlexdiscountApp::getHelper()->getProductId($item, 'sku') : '-p' . shopFlexdiscountApp::getHelper()->getProductId($item));            if (!isset($stock_logs[$key])) {                unset($items[$k]);                continue;            }            if (!self::execute_operator($params['op'], $start, strtotime($stock_logs[$key]))) {                unset($items[$k]);            }        }        return $items;    }    private static function filter_by_is_product_stock_change($items, $params)    {        $action = ifempty($params, 'field', 'increase');        $period = shopFlexdiscountHelper::getPeriod(!empty($params['period_type']) ? $params['period_type'] : 'alltime', $params);        $stock_logs = self::getProductStockChange($items, $params['sum_type'], $action, $period, true);        $start = ifset($period, 'start', '');        $end = ifset($period, 'end', '');        $datetime_key = '-d-' . $start . '-' . $end;        foreach ($items as $k => $item) {            $key = $params['sum_type'] . '-' . $action . $datetime_key . ($params['sum_type'] == 'not_sum' ? '-s' . shopFlexdiscountApp::getHelper()->getProductId($item, 'sku') : '-p' . shopFlexdiscountApp::getHelper()->getProductId($item));            if (!self::execute_operator($params['op'], isset($stock_logs[$key]) ? 1 : 0, 1)) {                unset($items[$k]);            }        }        return $items;    }    private static function filter_by_product_margin($items, $params)    {        foreach ($items as $k => $item) {            $item['primary_purchase_price'] = isset($item['primary_purchase_price']) ? $item['primary_purchase_price'] : 0;            if (!self::execute_operator($params['op'], ($item['primary_price'] - $item['primary_purchase_price']), $params['value'])) {                unset($items[$k]);            }        }        return $items;    }    private static function filter_by_product_margin_perc($items, $params)    {        foreach ($items as $k => $item) {            $item['primary_purchase_price'] = isset($item['primary_purchase_price']) ? $item['primary_purchase_price'] : 0;            if ((float) $item['primary_price'] !== (float) 0) {                $percent = ($item['primary_price'] - $item['primary_purchase_price']) * 100 / $item['primary_price'];                if (!self::execute_operator($params['op'], $percent, $params['value'])) {                    unset($items[$k]);                }            } else {                unset($items[$k]);            }        }        return $items;    }    private static function filter_by_product_margin_comp($items, $params)    {        foreach ($items as $k => $item) {            $item['primary_compare_price'] = isset($item['primary_compare_price']) ? $item['primary_compare_price'] : 0;            if (!self::execute_operator($params['op'], ($item['primary_compare_price'] - $item['primary_price']), $params['value'])) {                unset($items[$k]);            }        }        return $items;    }    private static function filter_by_product_margin_comp_perc($items, $params)    {        foreach ($items as $k => $item) {            $item['primary_compare_price'] = isset($item['primary_compare_price']) ? $item['primary_compare_price'] : 0;            if ((float) $item['primary_compare_price'] !== (float) 0) {                $percent = ($item['primary_compare_price'] - $item['primary_price']) * 100 / $item['primary_compare_price'];                if (!self::execute_operator($params['op'], $percent, $params['value'])) {                    unset($items[$k]);                }            } else {                unset($items[$k]);            }        }        return $items;    }    private static function filter_by_product_margin_trade_perc($items, $params)    {        foreach ($items as $k => $item) {            $item['primary_purchase_price'] = isset($item['primary_purchase_price']) ? $item['primary_purchase_price'] : 0;            if ((float) $item['primary_purchase_price'] !== (float) 0) {                $percent = ($item['primary_price'] - $item['primary_purchase_price']) * 100 / $item['primary_purchase_price'];                if (!self::execute_operator($params['op'], $percent, $params['value'])) {                    unset($items[$k]);                }            } else {                unset($items[$k]);            }        }        return $items;    }    private static function filter_by_product_margin_com